WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/38.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/38.0/apex.js")} 

var records = {!GETRECORDIDS($ObjectType.Lead)}; 
var RecordIds=''; 
if (records[0] == null) 
{ 
alert('Please select a record'); 
} 
else 
{ 
for (var n=0; n<records.length; n++) 
{ 
if(RecordIds== '') 
RecordIds= records[n]; 
else 
RecordIds= RecordIds+ "," + records[n] ; 
} 
window.location.href = '/apex/Kcloud_Actions__MassUpdate?SObject=Lead'+'&ToUpdateIds='+RecordIds+ '&retURL='+ window.location.href; 
}
